    }

    return (
      <section id="about">
      <div className="row">
         <div className="five columns">
         <div class="photo_frame">
             <img class="photo" src="./images/profilepic.jpeg" />
          </div>
         </div>
         <div className="seven columns main-col">
            <div class="about-text">
            <h1><span class="wave">ðŸ‘‹</span> Hi There!</h1>
            <p class="text">{bio1}</p>
            <p class="text">{bio2}</p>      
            <br />
