            <a href="https://drive.google.com/file/d/1KLGwoUn9cHL8Gvkp226xAXo9tx-Elwxf/view?usp=sharing" >Download Resume</a></button>
            </div>
            </div>
         </div>

   </section>
    );
  }
}

export default About;
